package kotaro1110;

public class Task14 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a = 1; a <=3; a ++){
			for(int b =2;b>=a;b --) {
			System.out.print(" ");
			} 
			for(int c = 1; c<=a; c++) {
			System.out.print(c);
			}
			System.out.println();
		}
	}
}
