package kotaro1110;

public class Task20 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a = 0; a<=2; a ++){
			for(int b =0;b<a;b ++) {
			System.out.print(" ");
			}
			for(int c =2;c>=a;c--) {
				System.out.print(1);
				}
			System.out.println();
		}
	}
}
