package kotaro1110;

public class Task21 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a = 3; a>=1; a --){
			for(int b =3;b>a;b --) {
			System.out.print(" ");
			}
			for(int c =1;c<=a;c++) {
				System.out.print(c);
				}
			System.out.println();
		}
	}
}
